span.script:
 /*************sale dashboard***************************/

//var input = $("#payment_tables_input").text();
var input=document.getElementById("sale_tables_input");
var jsonData1 = JSON.parse(input.value);

var chartInstance1 = new FusionCharts({

    type: 'column3d',
    width: '700', // Width of the chart
    height: '400', // Height of the chart
    dataFormat: 'json', // Data type
    renderAt:'chart_sale', //container where the chart will render
    dataSource: {
        "chart": {
            "caption": "Sales of month per Branch",
            "subCaption": "In CFA",
            "xAxisName": "Branch",
            "yAxisName": "Amount of sale",
            "numberSuffix": " CFA",
            "theme": "fusion",
            "labelDisplay": "rotate",
            "slantLabel": "1",
        },
        // Chart Data
        "data": jsonData1
    }
});

// Render
chartInstance1.render();


 /*************end sale dashboard***************************/


